#!/bin/bash
# Schedule Feb 9-15 Threads content to Postiz

API_KEY="75453381e58be37ec19683c7c921e91bd7d3cc0adfbcc609e881c861e20e5437"
THREADS_ID="cmlbamgkt008som0y7z6sl86w"

BASE_URL="https://api.postiz.com/public/v1"

schedule_post() {
    local date_str=$1
    local content=$2
    
    curl -s -X POST "$BASE_URL/posts" \
        -H "Authorization: $API_KEY" \
        -H "Content-Type: application/json" \
        -d "{
            \"type\": \"schedule\",
            \"date\": \"$date_str\",
            \"shortLink\": false,
            \"tags\": [\"feb9-15\", \"auto-scheduled\"],
            \"posts\": [{
                \"integration\": {\"id\": \"$THREADS_ID\"},
                \"value\": [{\"content\": \"$content\"}]
            }]
        }" | jq -r '.id // .msg'
}

echo "=== Scheduling Threads Posts ==="

